window.addEventListener('load', function(e) {
  return window.applicationCache.addEventListener('updateready', function(e) {
    console.log(window.applicationCache.status);
    if (window.applicationCache.status === window.applicationCache.UPDATEREADY) {
      if (confirm('Update beschikbaar, laden?')) {
        return window.location.reload();
      }
    }
  });
});

window.hideModal = function() {
  CurrentUser.set('modal', _.extend(CurrentUser.get('modal'), {
    open: false
  }));
  CurrentUser.trigger('change');
  return setTimeout(function() {
    return CurrentUser.set({
      modal: {
        open: false
      }
    });
  }, 500);
};

window.showModal = function(title, body, buttons) {
  if (buttons == null) {
    buttons = {};
  }
  return CurrentUser.set({
    modal: {
      open: true,
      title: title,
      body: body,
      buttons: buttons
    }
  });
};

window.openFile = function(uploadModel) {
  if ((typeof cordova !== "undefined" && cordova !== null) && (cordova.plugins != null) && (cordova.plugins.FileOpener != null)) {
    return cordova.plugins.FileOpener.openFile(uploadModel.get('path'));
  }
};

