module.exports = Backbone.Model.extend({
  url: apiUrl + "users/sign_in",
  remote: true,
  signedIn: function() {
    return this.get('auth_token') != null;
  },
  defaults: function() {
    var json;
    json = localStorage.getItem('currentUser');
    if ((json != null) && json.isJson()) {
      return JSON.parse(json);
    }
    return {};
  },
  signIn: function(onSuccess, onError) {
    var self;
    self = this;
    return this.save(null, {
      success: function(model, data, xhr) {
        localStorage.setItem("currentUser", JSON.stringify(model.attributes));
        return Router.navigate('/index', {
          trigger: true
        });
      }
    });
  },
  signOut: function() {
    if (!this.signedIn()) {
      return;
    }
    this.clear();
    localStorage.removeItem('currentUser');
    return Router.navigate('/login', {
      trigger: true
    });
  }
});

